import React, { Component } from 'react'
import {Bootstrap, Grid, Row, Col} from 'react-bootstrap';
import Firebase from 'firebase';
import firebase from './firebase';

class Upload extends Component {
  state={
    image: null,
    style: '',
    color: '',
    url: ''
  }

  onChange = (e) => {
    const state = this.state
    state[e.target.image] = e.target.value;
    this.setState(state);
  }

  handleChange = (e) => {
    if(e.target.files) {
      this.setState({
        image:e.target.files

      })
    }
    console.log(e.target.files[0]);
  }

  handleUpload = () => {
    const {image} = this.state;
    const uploadTask = firebase.storage().ref(`images/${image.name}`).put(this.state.image)
    uploadTask.on('state changed', (snapshot) =>{console.log(snapshot)},
    (err) => {console.log(err);},
    () => {firebase.storage().ref('images').child(image.name).getDownloadURL().then(url => {
      console.log(url);
    })})

  }



  render() {
    return (
      <>
        <form>
          {/* <input value={input} onChange={event => setInput(event.target.value)} /> */}
          <input className="upload-img" type="file" onChange={this.handleChange} />
          <img src={this.state.url} />
          
          <select className="upload-select" multiple title="Choose one of the following...">
            <option>White</option>
            <option>Black</option>
            <option>Blue</option>
            <option>Navy</option>
            <option>Red</option>
            <option>Yellow</option>
          </select>
          <button className="upload-cancel">Cancel</button>

          <button className="upload-submit-img" disabled={!input} onClick={this.handleUpload} >Upload Image First</button>
          <button className="upload-submit" disabled={!input} onClick={this.onSubmit} >Submit</button>

        </form>
      </>
    )
  }
}

export default Upload;
